void XCurve25519_donna_InterruptEnable(XCurve25519_donna *InstancePtr, u32 Mask) {
    u32 Register;

    Xil_AssertVoid(InstancePtr != NULL);
    Xil_AssertVoid(InstancePtr->IsReady == XIL_COMPONENT_IS_READY);

    Register =  XCurve25519_donna_ReadReg(InstancePtr->Axilites_BaseAddress, XCURVE25519_DONNA_AXILITES_ADDR_IER);
    XCurve25519_donna_WriteReg(InstancePtr->Axilites_BaseAddress, XCURVE25519_DONNA_AXILITES_ADDR_IER, Register | Mask);
}

void XCurve25519_donna_InterruptDisable(XCurve25519_donna *InstancePtr, u32 Mask) {
    u32 Register;

    Xil_AssertVoid(InstancePtr != NULL);
    Xil_AssertVoid(InstancePtr->IsReady == XIL_COMPONENT_IS_READY);

    Register =  XCurve25519_donna_ReadReg(InstancePtr->Axilites_BaseAddress, XCURVE25519_DONNA_AXILITES_ADDR_IER);
    XCurve25519_donna_WriteReg(InstancePtr->Axilites_BaseAddress, XCURVE25519_DONNA_AXILITES_ADDR_IER, Register & (~Mask));
}

void XCurve25519_donna_InterruptClear(XCurve25519_donna *InstancePtr, u32 Mask) {
    Xil_AssertVoid(InstancePtr != NULL);
    Xil_AssertVoid(InstancePtr->IsReady == XIL_COMPONENT_IS_READY);

    XCurve25519_donna_WriteReg(InstancePtr->Axilites_BaseAddress, XCURVE25519_DONNA_AXILITES_ADDR_ISR, Mask);
}

