    int_signature_we0    <= signature_we0;
    int_signature_be0    <= SHIFT_LEFT(TO_UNSIGNED(1, 4), TO_INTEGER(UNSIGNED(signature_address0(1 downto 0))));
    int_signature_d0     <= UNSIGNED(signature_d0) & UNSIGNED(signature_d0) & UNSIGNED(signature_d0) & UNSIGNED(signature_d0);
    signature_q0         <= STD_LOGIC_VECTOR(SHIFT_RIGHT(int_signature_q0, TO_INTEGER(int_signature_shift) * 8)(7 downto 0));
    int_signature_address1 <= raddr(5 downto 2) when ar_hs = '1' else waddr(5 downto 2);
    int_signature_ce1    <= '1' when ar_hs = '1' or (int_signature_write = '1' and WVALID  = '1') else '0';
    int_signature_we1    <= '1' when int_signature_write = '1' and WVALID = '1' else '0';
    int_signature_be1    <= UNSIGNED(WSTRB);
    int_signature_d1     <= UNSIGNED(WDATA);
    -- public_key
    int_public_key_address0 <= SHIFT_RIGHT(UNSIGNED(public_key_address0), 2)(2 downto 0);
    int_public_key_ce0   <= public_key_ce0;
    int_public_key_we0   <= '0';
    int_public_key_be0   <= (others => '0');
    int_public_key_d0    <= (others => '0');
    public_key_q0        <= STD_LOGIC_VECTOR(SHIFT_RIGHT(int_public_key_q0, TO_INTEGER(int_public_key_shift) * 8)(7 downto 0));
    int_public_key_address1 <= raddr(4 downto 2) when ar_hs = '1' else waddr(4 downto 2);
    int_public_key_ce1   <= '1' when ar_hs = '1' or (int_public_key_write = '1' and WVALID  = '1') else '0';
    int_public_key_we1   <= '1' when int_public_key_write = '1' and WVALID = '1' else '0';
    int_public_key_be1   <= UNSIGNED(WSTRB);
    int_public_key_d1    <= UNSIGNED(WDATA);
    -- private_key
    int_private_key_address0 <= SHIFT_RIGHT(UNSIGNED(private_key_address0), 2)(3 downto 0);
    int_private_key_ce0  <= private_key_ce0;
    int_private_key_we0  <= '0';
    int_private_key_be0  <= (others => '0');
    int_private_key_d0   <= (others => '0');
    private_key_q0       <= STD_LOGIC_VECTOR(SHIFT_RIGHT(int_private_key_q0, TO_INTEGER(int_private_key_shift) * 8)(7 downto 0));
    int_private_key_address1 <= raddr(5 downto 2) when ar_hs = '1' else waddr(5 downto 2);
    int_private_key_ce1  <= '1' when ar_hs = '1' or (int_private_key_write = '1' and WVALID  = '1') else '0';
    int_private_key_we1  <= '1' when int_private_key_write = '1' and WVALID = '1' else '0';
    int_private_key_be1  <= UNSIGNED(WSTRB);
    int_private_key_d1   <= UNSIGNED(WDATA);

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_signature_read <= '0';
            elsif (ACLK_EN = '1') then
                if (ar_hs = '1' and raddr >= ADDR_SIGNATURE_BASE and raddr <= ADDR_SIGNATURE_HIGH) then
                    int_signature_read <= '1';
                else
                    int_signature_read <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_signature_write <= '0';
            elsif (ACLK_EN = '1') then
                if (aw_hs = '1' and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) >= ADDR_SIGNATURE_BASE and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) <= ADDR_SIGNATURE_HIGH) then
                    int_signature_write <= '1';
                elsif (WVALID = '1') then
                    int_signature_write <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ACLK_EN = '1') then
                if (signature_ce0 = '1') then
                    int_signature_shift <= UNSIGNED(signature_address0(1 downto 0));
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_public_key_read <= '0';
            elsif (ACLK_EN = '1') then
                if (ar_hs = '1' and raddr >= ADDR_PUBLIC_KEY_BASE and raddr <= ADDR_PUBLIC_KEY_HIGH) then
                    int_public_key_read <= '1';
                else
                    int_public_key_read <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_public_key_write <= '0';
            elsif (ACLK_EN = '1') then
                if (aw_hs = '1' and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) >= ADDR_PUBLIC_KEY_BASE and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) <= ADDR_PUBLIC_KEY_HIGH) then
                    int_public_key_write <= '1';
                elsif (WVALID = '1') then
                    int_public_key_write <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ACLK_EN = '1') then
                if (public_key_ce0 = '1') then
                    int_public_key_shift <= UNSIGNED(public_key_address0(1 downto 0));
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_private_key_read <= '0';
            elsif (ACLK_EN = '1') then
                if (ar_hs = '1' and raddr >= ADDR_PRIVATE_KEY_BASE and raddr <= ADDR_PRIVATE_KEY_HIGH) then
                    int_private_key_read <= '1';
                else
                    int_private_key_read <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                int_private_key_write <= '0';
            elsif (ACLK_EN = '1') then
                if (aw_hs = '1' and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) >= ADDR_PRIVATE_KEY_BASE and UNSIGNED(AWADDR(ADDR_BITS-1 downto 0)) <= ADDR_PRIVATE_KEY_HIGH) then
                    int_private_key_write <= '1';
                elsif (WVALID = '1') then
                    int_private_key_write <= '0';
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ACLK_EN = '1') then
                if (private_key_ce0 = '1') then
                    int_private_key_shift <= UNSIGNED(private_key_address0(1 downto 0));
                end if;
            end if;
        end if;
    end process;


end architecture behave;

library IEEE;
USE IEEE.std_logic_1164.all;
USE IEEE.numeric_std.all;

entity ed25519_sign_AXILiteS_s_axi_ram is
    generic (
        BYTES   : INTEGER :=4;
        DEPTH   : INTEGER :=256;
        AWIDTH  : INTEGER :=8);
    port (
        clk0    : in  STD_LOGIC;
        address0: in  UNSIGNED(AWIDTH-1 downto 0);
        ce0     : in  STD_LOGIC;
        we0     : in  STD_LOGIC;
        be0     : in  UNSIGNED(BYTES-1 downto 0);
        d0      : in  UNSIGNED(BYTES*8-1 downto 0);
        q0      : out UNSIGNED(BYTES*8-1 downto 0);
        clk1    : in  STD_LOGIC;
        address1: in  UNSIGNED(AWIDTH-1 downto 0);
        ce1     : in  STD_LOGIC;
        we1     : in  STD_LOGIC;
        be1     : in  UNSIGNED(BYTES-1 downto 0);
        d1      : in  UNSIGNED(BYTES*8-1 downto 0);
        q1      : out UNSIGNED(BYTES*8-1 downto 0));

end entity ed25519_sign_AXILiteS_s_axi_ram;

architecture behave of ed25519_sign_AXILiteS_s_axi_ram is
    signal address0_tmp : UNSIGNED(AWIDTH-1 downto 0);
    signal address1_tmp : UNSIGNED(AWIDTH-1 downto 0);
    type RAM_T is array (0 to DEPTH - 1) of UNSIGNED(BYTES*8 - 1 downto 0);
    shared variable mem : RAM_T := (others => (others => '0'));
begin

    process (address0)
    begin
    address0_tmp <= address0;
    --synthesis translate_off
          if (address0 > DEPTH-1) then
              address0_tmp <= (others => '0');
          else
              address0_tmp <= address0;
          end if;
    --synthesis translate_on
    end process;

    process (address1)
    begin
    address1_tmp <= address1;
    --synthesis translate_off
          if (address1 > DEPTH-1) then
              address1_tmp <= (others => '0');
          else
              address1_tmp <= address1;
          end if;
    --synthesis translate_on
    end process;

    --read port 0
    process (clk0) begin
        if (clk0'event and clk0 = '1') then
            if (ce0 = '1') then
                q0 <= mem(to_integer(address0_tmp));
            end if;
        end if;
    end process;

    --read port 1
    process (clk1) begin
        if (clk1'event and clk1 = '1') then
            if (ce1 = '1') then
                q1 <= mem(to_integer(address1_tmp));
            end if;
        end if;
    end process;

    gen_write : for i in 0 to BYTES - 1 generate
    begin
        --write port 0
        process (clk0)
        begin
            if (clk0'event and clk0 = '1') then
                if (ce0 = '1' and we0 = '1' and be0(i) = '1') then
                    mem(to_integer(address0_tmp))(8*i+7 downto 8*i) := d0(8*i+7 downto 8*i);
                end if;
            end if;
        end process;

        --write port 1
        process (clk1)
        begin
            if (clk1'event and clk1 = '1') then
                if (ce1 = '1' and we1 = '1' and be1(i) = '1') then
                    mem(to_integer(address1_tmp))(8*i+7 downto 8*i) := d1(8*i+7 downto 8*i);
                end if;
            end if;
        end process;

    end generate;

end architecture behave;


